import React, { useContext } from "react";
import { useContextCart } from "../contexts/CartContext";
import "../styles/pages/CartPage.css";

const CartPage = () => {
	const { cartItems, updateCartItem, removeFromCart } = useContextCart();

	const handleQuantityChange = (id, type) => {
		updateCartItem(id, type);
	};

	return (
		<div className="cart-page">
			<h2>Your Cart</h2>
			<div className="cart-items">
				{cartItems.length === 0 ? (
					<p>Your cart is empty</p>
				) : (
					cartItems.map((item) => (
						<div key={item.id} className="cart-item">
							<img src={`images/${item.image}`} alt={item.name} />
							<h3>{item.name}</h3>
							<p>{item.description}</p>
							<p>${item.price}</p>

							<div className="quantity-controls">
								<button
									className="quantity-btn"
									onClick={() => handleQuantityChange(item.id, "-")}
									style={{ backgroundColor: "#ccc" }}
								>
									-
								</button>
								<input
									type="text"
									value={item.quantity}
									onChange={(e) =>
										updateCartItem(item.id, parseInt(e.target.value))
									}
									className="quantity-input"
								/>
								<button
									className="quantity-btn"
									onClick={() => handleQuantityChange(item.id, "+")}
									style={{ backgroundColor: "#ccc" }}
								>
									+
								</button>
							</div>

							<button onClick={() => removeFromCart(item.id)} className="remove-item-btn">
								Remove
							</button>
						</div>
					))
				)}
			</div>
		</div>
	);
};

export default CartPage;
