import React from "react";
import { useContextCart } from "../contexts/CartContext";
import "../styles/pages/CartPage.css";

const CartPage = () => {
	const { cartItems, updateCartItem, removeFromCart } = useContextCart();

	const handleQuantityChange = (id, type) => {
		updateCartItem(id, type);
	};

	const handleRemoveItem = (id) => {
		removeFromCart(id);
	};

	const calculateTotal = () => {
		return cartItems.reduce((total, item) => total + item.price * item.quantity, 0).toFixed(2);
	};

	const handleUpdateCart = () => {
	};

	return (
		<div className="cart-page">
			<h2>Your Cart</h2>
			<div className="cart-items">
				{cartItems.length === 0 ? (
					<p>Your cart is empty</p>
				) : (
					cartItems.map((item) => (
						<div key={item.id} className="cart-item">
							<img src={`images/${item.image}`} alt={item.name} />
							<div className="item-details">
								<h3>{item.name}</h3>
								<p>${item.price}</p>

								<div className="quantity-controls">
									<button
										className="quantity-btn"
										onClick={() => handleQuantityChange(item.id, "-")}
									>
										-
									</button>
									<input
										type="text"
										value={item.quantity}
										className="quantity-input"
										readOnly
									/>
									<button
										className="quantity-btn"
										onClick={() => handleQuantityChange(item.id, "+")}
									>
										+
									</button>
								</div>
							</div>

							<button
								className="remove-btn"
								onClick={() => handleRemoveItem(item.id)}
							>
								Remove
							</button>
						</div>
					))
				)}
			</div>

			<div className="cart-total">
				<h3>Total: ${calculateTotal()}</h3>
			</div>

			<div className="update-cart-btn">
				<button onClick={handleUpdateCart} className="update-btn">
					Update Cart
				</button>
			</div>
		</div>
	);
};

export default CartPage;
