import React, { useState, useContext } from "react";
import { CartContext } from "../contexts/CartContext";
import "../../src/styles/pages/CartPage.css";

const CartPage = () => {
	const { cart, updateCart, removeFromCart } = useContext(CartContext);
	const [quantity, setQuantity] = useState({});

	const handleIncrement = (id) => {
		setQuantity((prev) => ({ ...prev, [id]: (prev[id] || 0) + 1 }));
	};

	const handleDecrement = (id) => {
		setQuantity((prev) => ({
			...prev,
			[id]: prev[id] > 1 ? prev[id] - 1 : 1,
		}));
	};

	const handleChangeQuantity = (id, value) => {
		if (value >= 1) setQuantity((prev) => ({ ...prev, [id]: value }));
	};

	const handleUpdateCart = (id) => {
		updateCart(id, quantity[id] || 1);
	};

	return (
		<div className="cart-page">
			<h2>Your Cart</h2>
			<div className="cart-items">
				{cart.length === 0 ? (
					<p>Your cart is empty.</p>
				) : (
					cart.map((item) => (
						<div key={item.id} className="cart-item-container">
							<div className="cart-item">
								<img src={item.image} alt={item.name} />
								<div>
									<h3>{item.name}</h3>
									<p>{item.description}</p>
									<p>${item.price}</p>
									<div className="quantity-controls">
										<button onClick={() => handleDecrement(item.id)}>-</button>
										<input
											type="number"
											value={quantity[item.id] || 1}
											onChange={(e) =>
												handleChangeQuantity(item.id, parseInt(e.target.value, 10))
											}
											min="1"
										/>
										<button onClick={() => handleIncrement(item.id)}>+</button>
									</div>
									<div className="update-controls">
										<button onClick={() => handleUpdateCart(item.id)}>Update</button>
										<button onClick={() => removeFromCart(item.id)} className="delete-btn">
											Delete
										</button>
									</div>
								</div>
							</div>
						</div>
					))
				)}
			</div>
		</div>
	);
};

export default CartPage;
