import React, { useContext, useState } from "react";
import { CartContext } from "../contexts/CartContext";
import "../styles/pages/CartPage.css";

const CartPage = () => {
	const { cart, removeItem, updateQuantity } = useContext(CartContext);
	const [quantities, setQuantities] = useState(
		cart.reduce((acc, item) => {
			acc[item.id] = item.quantity;
			return acc;
		}, {})
	);

	const handleQuantityChange = (id, action) => {
		setQuantities((prev) => {
			const newQuantity = action === "increase" ? prev[id] + 1 : prev[id] - 1;
			return {
				...prev,
				[id]: newQuantity > 0 ? newQuantity : 1,
			};
		});
	};

	const handleUpdate = (id) => {
		updateQuantity(id, quantities[id]);
	};

	return (
		<div className="cart-page">
			<h2>Your Cart</h2>
			{cart.length === 0 ? (
				<p>Your cart is empty</p>
			) : (
				<div className="cart-container">
					{cart.map((item) => (
						<div key={item.id} className="cart-item-box">
							<img src={`images/${item.image}`} alt={item.name} />
							<h3>{item.name}</h3>
							<p>{item.description}</p>
							<p>${item.price}</p>

							<div className="quantity-container">
								<button onClick={() => handleQuantityChange(item.id, "decrease")}>
									-
								</button>
								<input
									type="number"
									value={quantities[item.id]}
									onChange={(e) =>
										setQuantities((prev) => ({
											...prev,
											[item.id]: Math.max(1, e.target.value),
										}))
									}
									min="1"
								/>
								<button onClick={() => handleQuantityChange(item.id, "increase")}>
									+
								</button>
							</div>

							<button onClick={() => handleUpdate(item.id)}>Update</button>
							<button onClick={() => removeItem(item.id)}>Remove</button>
						</div>
					))}
				</div>
			)}
		</div>
	);
};

export default CartPage;
