import React, { useState, useEffect } from 'react';
import { useContextCart } from '../contexts/CartContext';
import "../../src/styles/pages/CartPage.css";

const CartPage = () => {
  const { cartItems, updateCartItem, removeFromCart } = useContextCart();
  const [updatedItemId, setUpdatedItemId] = useState(null);
  const [total, setTotal] = useState(0);

  // Function to calculate the total cost
  useEffect(() => {
    const calculateTotal = () => {
      const totalCost = cartItems.reduce(
        (acc, item) => acc + item.price * item.quantity, 0
      );
      setTotal(totalCost.toFixed(2));
    };
    calculateTotal();
  }, [cartItems]);

  const handleUpdateClick = (itemId, newQuantity) => {
    updateCartItem(itemId, newQuantity);
    setUpdatedItemId(itemId);
    setTimeout(() => setUpdatedItemId(null), 2000); // Reset "Item Updated" text after 2 seconds
  };

  return (
    <div className="cart-page">
      <h2>Your Cart</h2>
      {cartItems.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <div className="cart-items">
          {cartItems.map((item) => (
            <div key={item.id} className="cart-item">
              <img src={`images/${item.image}`} alt={item.name} />
              <div>
                <h3>{item.name}</h3>
                <p>{item.description}</p>
                <p>${item.price}</p>
                <div className="quantity-control">
                  <button
                    className="quantity-button"
                    onClick={() => updateCartItem(item.id, item.quantity - 1)}
                  >
                    -
                  </button>
                  <input
                    type="number"
                    value={item.quantity}
                    onChange={(e) => updateCartItem(item.id, parseInt(e.target.value))}
                    min="1"
                  />
                  <button
                    className="quantity-button"
                    onClick={() => updateCartItem(item.id, item.quantity + 1)}
                  >
                    +
                  </button>
                </div>
                <button
                  className="update-button"
                  onClick={() => handleUpdateClick(item.id, item.quantity)}
                >
                  {updatedItemId === item.id ? "Item Updated" : "Update"}
                </button>
                <button
                  className="remove-button"
                  onClick={() => removeFromCart(item.id)}
                >
                  Remove
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
      <div className="total-container">
        <h3>Total: ${total}</h3>
      </div>
    </div>
  );
};

export default CartPage;
