import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../../src/styles/pages/ShopPage.css";

const ShopPage = () => {
  const [items, setItems] = useState([
    { id: 1, name: "Apple", description: "Fresh red apple", price: 2.5, image: "apple.jpg", quantity: 0 },
    { id: 2, name: "Banana", description: "Fresh ripe bananas", price: 1.8, image: "banana.jpg", quantity: 0 },
    { id: 3, name: "Carrot", description: "Fresh organic carrots", price: 1.2, image: "carrot.jpg", quantity: 0 },
    { id: 4, name: "Grapes", description: "Fresh green grapes", price: 2.8, image: "grapes.jpg", quantity: 0 },
    { id: 5, name: "Lemon", description: "Fresh yellow lemons", price: 1.5, image: "lemon.jpg", quantity: 0 },
    { id: 6, name: "Orange", description: "Fresh juicy oranges", price: 2, image: "orange.jpg", quantity: 0 },
    { id: 7, name: "Pineapple", description: "Fresh tropical pineapple", price: 3.5, image: "pineapple.jpg", quantity: 0 },
    { id: 8, name: "Mango", description: "Sweet ripe mangoes", price: 2.2, image: "mango.jpg", quantity: 0 },
    { id: 9, name: "Peach", description: "Fresh juicy peaches", price: 2.7, image: "peach.jpg", quantity: 0 },
    { id: 10, name: "Strawberry", description: "Fresh ripe strawberries", price: 3, image: "strawberry.jpg", quantity: 0 },
    { id: 11, name: "Plum", description: "Fresh sweet plums", price: 2.4, image: "plum.jpg", quantity: 0 },
    { id: 12, name: "Avocado", description: "Fresh creamy avocado", price: 2.9, image: "avocado.jpg", quantity: 0 },
  ]);

  const handleAddToCart = (item) => {
    setItems(
      items.map((i) =>
        i.id === item.id
          ? { ...i, quantity: i.quantity === 0 ? 1 : i.quantity }
          : i
      )
    );
  };

  const handleChangeQuantity = (id, value) => {
    if (value >= 0) {
      setItems(
        items.map((i) =>
          i.id === id ? { ...i, quantity: value } : i
        )
      );
    }
  };

  return (
    <div className="shop-page">
      <h2>Shop Page</h2>
      <div className="shop-items">
        {items.map((item) => (
          <div key={item.id} className="shop-item">
            <img src={`images/${item.image}`} alt={item.name} />
            <h3>{item.name}</h3>
            <p>{item.description}</p>
            <p>${item.price}</p>
            {item.quantity === 0 ? (
              <button onClick={() => handleAddToCart(item)}>Add to Cart</button>
            ) : (
              <div>
                <button onClick={() => handleAddToCart(item)}>Item Added to Cart</button>
                <div className="quantity-container">
                  <button onClick={() => handleChangeQuantity(item.id, item.quantity - 1)}>-</button>
                  <input
                    type="number"
                    value={item.quantity}
                    onChange={(e) => handleChangeQuantity(item.id, parseInt(e.target.value))}
                    min="0"
                  />
                  <button onClick={() => handleChangeQuantity(item.id, item.quantity + 1)}>+</button>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
      <Link to="/cart">
        <button>View Cart</button>
      </Link>
    </div>
  );
};

export default ShopPage;
