import React, { useState, useEffect } from 'react';
import { useContextAuth } from '../contexts/AuthContext';
import { Link } from 'react-router-dom';
import '../../src/styles/pages/BlogPage.css';

const BlogPage = () => {
	const { user } = useContextAuth();
	const [posts, setPosts] = useState([]);
	const [comments, setComments] = useState([]);
	const [newComment, setNewComment] = useState("");

	useEffect(() => {
		const fetchPosts = async () => {
			const fetchedPosts = [
				{
					id: 1,
					author: "dannydo@test.com",
					title: "My First Blog Post",
					content: "This is the content of my first post.",
				},
				{
					id: 2,
					author: "someoneelse@test.com",
					title: "A Second Post",
					content: "Here's the content of a second post.",
				}
			];
			setPosts(fetchedPosts);
		};

		const fetchComments = async () => {
			const fetchedComments = [
				{ postId: 1, user: "dannydo@test.com", comment: "Great post!", id: 1 },
				{ postId: 1, user: "someoneelse@test.com", comment: "I agree, nice read.", id: 2 },
			];
			setComments(fetchedComments);
		};

		fetchPosts();
		fetchComments();
	}, []);

	const handleAddComment = (postId) => {
		if (newComment.trim()) {
			const newCommentObj = {
				postId: postId,
				user: user.email,
				comment: newComment,
				id: comments.length + 1,
			};
			setComments([...comments, newCommentObj]);
			setNewComment("");
		}
	};

	return (
		<div className="blog-page">
			<h2>Blog Page</h2>
			{posts.map((post) => (
				<div key={post.id} className="post">
					<h3><Link to={`/blog/${post.id}`}>{post.title}</Link></h3>
					<p>{post.content}</p>

					<div className="comments">
						{comments
							.filter((comment) => comment.postId === post.id)
							.map((comment) => (
								<div key={comment.id} className="comment">
									<p><strong>{comment.user}:</strong> {comment.comment}</p>
								</div>
							))}
					</div>

					{user && (
						<div className="add-comment">
							<input
								type="text"
								value={newComment}
								onChange={(e) => setNewComment(e.target.value)}
								placeholder="Add a comment"
							/>
							<button onClick={() => handleAddComment(post.id)}>Submit Comment</button>
						</div>
					)}
				</div>
			))}
		</div>
	);
};

export default BlogPage;
