import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../../src/styles/pages/ShopPage.css";

const ShopPage = () => {
	const [items, setItems] = useState([
		{ id: 1, name: "Apple", description: "Fresh red apple", price: 2.5, image: "apple.jpg", quantity: 0, addedToCart: false },
		{ id: 2, name: "Banana", description: "Fresh ripe bananas", price: 1.8, image: "banana.jpg", quantity: 0, addedToCart: false },
		{ id: 3, name: "Carrot", description: "Fresh organic carrots", price: 1.2, image: "carrot.jpg", quantity: 0, addedToCart: false },
		{ id: 4, name: "Grapes", description: "Fresh green grapes", price: 2.8, image: "grapes.jpg", quantity: 0, addedToCart: false },
		{ id: 5, name: "Lemon", description: "Fresh yellow lemons", price: 1.5, image: "lemon.jpg", quantity: 0, addedToCart: false },
		{ id: 6, name: "Orange", description: "Fresh juicy oranges", price: 2, image: "orange.jpg", quantity: 0, addedToCart: false },
		{ id: 7, name: "Pineapple", description: "Fresh tropical pineapple", price: 3.5, image: "pineapple.jpg", quantity: 0, addedToCart: false },
		{ id: 8, name: "Mango", description: "Sweet ripe mangoes", price: 2.2, image: "mango.jpg", quantity: 0, addedToCart: false },
		{ id: 9, name: "Peach", description: "Fresh juicy peaches", price: 2.7, image: "peach.jpg", quantity: 0, addedToCart: false },
		{ id: 10, name: "Strawberry", description: "Fresh ripe strawberries", price: 3, image: "strawberry.jpg", quantity: 0, addedToCart: false },
		{ id: 11, name: "Plum", description: "Fresh sweet plums", price: 2.4, image: "plum.jpg", quantity: 0, addedToCart: false },
		{ id: 12, name: "Avocado", description: "Fresh creamy avocado", price: 2.9, image: "avocado.jpg", quantity: 0, addedToCart: false }
	]);

	// Function to handle adding an item to the cart
	const handleAddToCart = (id) => {
		const newItems = [...items];
		const item = newItems.find((item) => item.id === id);
		if (item) {
			item.addedToCart = true;  // Mark the item as added to the cart
		}
		setItems(newItems);
	};

	// Function to change the quantity of an item
	const handleChangeQuantity = (id, amount) => {
		const newItems = [...items];
		const item = newItems.find((item) => item.id === id);
		if (item) {
			item.quantity = Math.max(0, item.quantity + amount);  // Prevent negative quantities
			item.addedToCart = false; // Reset added to cart state when quantity changes
		}
		setItems(newItems);
	};

	return (
		<div className="shop-page">
			<h2>Shop Page</h2>
			<div className="shop-items">
				{items.map((item) => (
					<div key={item.id} className="shop-item">
						<img src={`images/${item.image}`} alt={item.name} />
						<h3>{item.name}</h3>
						<p>{item.description}</p>
						<p>${item.price}</p>

						<div className="quantity-controls">
							<button
								className="quantity-button"
								onClick={() => handleChangeQuantity(item.id, -1)}
							>
								-
							</button>
							<input
								className="quantity-input"
								type="number"
								value={item.quantity}
								onChange={(e) =>
									handleChangeQuantity(
										item.id,
										parseInt(e.target.value, 10) - item.quantity
									)
								}
								max="99"
								min="0"
							/>
							<button
								className="quantity-button"
								onClick={() => handleChangeQuantity(item.id, 1)}
							>
								+
							</button>
						</div>
						<button
							className="add-to-cart-button"
							onClick={() => handleAddToCart(item.id)}
						>
							{item.addedToCart ? "Item Added to Cart!" : "Add to Cart"}
						</button>
					</div>
				))}
			</div>
		</div>
	);
};

export default ShopPage;
