import React, { useContext, useState } from "react";
import { useContextCart } from "../contexts/CartContext";
import "../styles/pages/CartPage.css";

const CartPage = () => {
	const { cartItems, updateCartItem, removeFromCart } = useContextCart();
	const [updatedQuantities, setUpdatedQuantities] = useState(
		cartItems.reduce((acc, item) => {
			acc[item.id] = item.quantity;
			return acc;
		}, {})
	);

	const handleQuantityChange = (id, type) => {
		setUpdatedQuantities((prev) => ({
			...prev,
			[id]: type === "+" ? prev[id] + 1 : type === "-" && prev[id] > 1 ? prev[id] - 1 : prev[id],
		}));
	};

	const handleInputChange = (id, value) => {
		if (value >= 1) {
			setUpdatedQuantities((prev) => ({
				...prev,
				[id]: value,
			}));
		}
	};

	const handleUpdateCart = (id) => {
		updateCartItem(id, updatedQuantities[id]);
	};

	return (
		<div className="cart-page">
			<h2>Your Cart</h2>
			<div className="cart-items">
				{cartItems.length === 0 ? (
					<p>Your cart is empty</p>
				) : (
					cartItems.map((item) => (
						<div key={item.id} className="cart-item">
							<img src={`images/${item.image}`} alt={item.name} />
							<h3>{item.name}</h3>
							<p>{item.description}</p>
							<p>${item.price}</p>

							<div className="quantity-controls">
								<button
									className="quantity-btn"
									onClick={() => handleQuantityChange(item.id, "-")}
									style={{ backgroundColor: "#ccc" }}
								>
									-
								</button>
								<input
									type="text"
									value={updatedQuantities[item.id] || item.quantity}
									onChange={(e) => handleInputChange(item.id, parseInt(e.target.value))}
									className="quantity-input"
								/>
								<button
									className="quantity-btn"
									onClick={() => handleQuantityChange(item.id, "+")}
									style={{ backgroundColor: "#ccc" }}
								>
									+
								</button>
							</div>

							<button onClick={() => handleUpdateCart(item.id)} className="update-item-btn">
								Update
							</button>

							<button onClick={() => removeFromCart(item.id)} className="remove-item-btn">
								Remove
							</button>
						</div>
					))
				)}
			</div>
		</div>
	);
};

export default CartPage;
